"use strict";angular.module("overwatch-hero-picker").component("owHeroList",{bindings:{onHeroSelection:"&"},template:'<input type=search ng-model=search placeholder="find a hero by name, type or description"><ul><li ng-repeat="hero in $ctrl.heroes | filter: search |orderBy: \'type\'"><button ng-click=$ctrl.selectHero(hero)><img ng-src={{hero.avatar}}><h2>{{hero.name}}</h2><p>{{hero.type}}: {{hero.description}}</p></button></li></ul>',controller:["$http",function(e){var r=this;e.get("data/heroes.json").then(function(e){return r.heroes=e.data}),this.selectHero=function(e){r.onHeroSelection(e)}}]});
