"use strict";angular.module("overwatch-hero-picker",[]);
"use strict";angular.module("overwatch-hero-picker").component("owHeroList",{template:'<input type=search ng-model=search placeholder="find a hero by name, type or description"><ul><li ng-repeat="hero in $ctrl.heroes |filter: search"><img ng-src={{hero.avatar}}><h2>{{hero.name}}</h2><p>{{hero.description}}</p></li></ul>',controller:["$http",function(e){var r=this;e.get("data/heroes.json").then(function(e){return r.heroes=e.data})}]});
"use strict";angular.module("overwatch-hero-picker").component("owHeroPicker",{template:"<ow-hero-list></ow-hero-list>"});